import React from "react";

export default function Stepper({ steps, current, setCurrent }) {
  return (
    <div>
      <nav className="flex items-center gap-6 border-b pb-4">
        {steps.slice(0, -1).map((s, idx) => {
          const stepNum = idx + 1;
          const active = stepNum === current;
          const done = stepNum < current;
          return (
            <div key={s.id} className="flex items-center gap-3">
              <button onClick={() => setCurrent(stepNum)} className={`flex items-center gap-2 ${active ? 'text-blue-600' : 'text-gray-500'}`}>
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${done ? 'bg-blue-600 text-white' : active ? 'border-2 border-blue-600' : 'bg-gray-100 text-gray-500'}`}>
                  {done ? '✓' : stepNum}
                </div>
                <div className="text-sm">{s.title}</div>
              </button>
              {idx < steps.length - 2 && <div className="w-8 h-[1px] bg-gray-200" />}
            </div>
          );
        })}
      </nav>
    </div>
  );
}
