import React, { useRef } from "react";

export default function CompanyInfo({ data, update, next }) {
  const logoRef = useRef();
  const bannerRef = useRef();

  const onFile = (e, key) => {
    const file = e.target.files?.[0];
    if (!file) return;
    update({ [key]: { file, url: URL.createObjectURL(file) } });
  };

  return (
    <div>
      <h2 className="text-lg font-semibold mb-4">Logo & Banner Image</h2>
      <div className="grid grid-cols-3 gap-6 items-start">
        <div className="col-span-1">
          <label className="block text-sm text-gray-600 mb-2">Upload Logo</label>
          <div className="relative border-dashed border-2 border-gray-200 rounded p-4 flex items-center justify-center">
            {data.logo?.url ? <img src={data.logo.url} alt="logo" className="max-h-20"/> : <div className="text-gray-400 text-sm">Browse photo or drop here</div>}
            <input ref={logoRef} onChange={(e)=>onFile(e,'logo')} className="absolute w-full h-full opacity-0 cursor-pointer" type="file" accept="image/*" />
          </div>
        </div>

        <div className="col-span-2">
          <label className="block text-sm text-gray-600 mb-2">Banner Image</label>
          <div className="relative border-dashed border-2 border-gray-200 rounded p-6 flex items-center justify-center">
            {data.banner?.url ? <img src={data.banner.url} alt="banner" className="w-full max-h-40 object-cover"/> : <div className="text-gray-400">Browse photo or drop here (recommended 1520x400)</div>}
            <input ref={bannerRef} onChange={(e)=>onFile(e,'banner')} className="absolute w-full h-full opacity-0 cursor-pointer" type="file" accept="image/*" />
          </div>
        </div>
      </div>

      <div className="mt-6">
        <label className="block text-sm text-gray-600">Company name</label>
        <input value={data.companyName} onChange={(e)=>update({companyName: e.target.value})} className="w-full border rounded p-2 mt-1" />
      </div>

      <div className="mt-4">
        <label className="block text-sm text-gray-600">About Us</label>
        <textarea value={data.about} onChange={(e)=>update({about: e.target.value})} className="w-full border rounded p-2 mt-1 h-28" placeholder="Write down about your company here."></textarea>
      </div>

      <div className="mt-6 flex justify-start gap-4">
        <button onClick={next} className="bg-blue-600 text-white px-4 py-2 rounded">Save & Next →</button>
      </div>
    </div>
  );
}
