import React from "react";
const platforms = [{ value: "facebook", label: "Facebook" },{ value: "twitter", label: "Twitter" },{ value: "instagram", label: "Instagram" },{ value: "youtube", label: "Youtube" },{ value: "linkedin", label: "LinkedIn" }];

export default function SocialLinks({ data, update, next, prev }) {
  const setSocial = (index, patch) => {
    const list = [...data.socials];
    list[index] = { ...list[index], ...patch };
    update({ socials: list });
  };
  const addSocial = () => update({ socials: [...data.socials, { platform: "facebook", url: "" }]});
  const removeSocial = (index) => update({ socials: data.socials.filter((_, i) => i !== index) });

  return (
    <div>
      <h2 className="text-lg font-semibold mb-4">Social Media Profile</h2>
      <div className="space-y-3">
        {data.socials.map((s, i) => (
          <div key={i} className="flex gap-3 items-center">
            <select value={s.platform} onChange={(e)=>setSocial(i, {platform: e.target.value})} className="w-40 border rounded p-2">
              {platforms.map(p => <option key={p.value} value={p.value}>{p.label}</option>)}
            </select>
            <input placeholder="Profile link/url..." value={s.url} onChange={(e)=>setSocial(i,{url:e.target.value})} className="flex-1 border rounded p-2" />
            <button onClick={()=>removeSocial(i)} className="p-2 border rounded bg-gray-50">✕</button>
          </div>
        ))}
      </div>

      <div className="mt-3">
        <button onClick={addSocial} className="py-2 px-3 bg-gray-100 rounded border">+ Add New Social Link</button>
      </div>

      <div className="mt-6 flex gap-4">
        <button onClick={prev} className="px-4 py-2 border rounded">Previous</button>
        <button onClick={next} className="bg-blue-600 text-white px-4 py-2 rounded">Save & Next →</button>
      </div>
    </div>
  );
}
