import React, { useState } from "react";
import Stepper from "../../components/Stepper";
import CompanyInfo from "./steps/CompanyInfo";
import FoundingInfo from "./steps/FoundingInfo";
import SocialLinks from "./steps/SocialLinks";
import Contact from "./steps/Contact";
import Complete from "./steps/Complete";

const steps = [
  { id: 1, title: "Company Info" },
  { id: 2, title: "Founding Info" },
  { id: 3, title: "Social Media Profile" },
  { id: 4, title: "Contact" },
  { id: 5, title: "Complete" },
];

export default function AccountSetup() {
  const [current, setCurrent] = useState(1);

  const [data, setData] = useState({
    logo: "",
    banner: "",
    companyName: "",
    about: "",
    orgType: "",
    industry: "",
    teamSize: "",
    year: "",
    website: "",
    vision: "",
    socials: [{ platform: "facebook", url: "" }],
    mapLocation: "",
    phoneCountry: "+91",
    phone: "",
    email: "",
  });

  const update = (patch) => setData((prev) => ({ ...prev, ...patch }));
  const next = () => setCurrent((c) => Math.min(steps.length, c + 1));
  const prev = () => setCurrent((c) => Math.max(1, c - 1));
  const submit = () => {
    console.log("Final data:", data); // replace with API call later
    next();
  };

  const progress = Math.round(((current - 1) / (steps.length - 1)) * 100);

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto p-8">
        <header className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="bg-blue-600 text-white p-2 rounded-md">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                <path
                  d="M6 7h12l1 12H5L6 7z"
                  stroke="white"
                  strokeWidth="1.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
                <path
                  d="M9 7V5a3 3 0 1 1 6 0v2"
                  stroke="white"
                  strokeWidth="1.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </div>
            <h1 className="text-xl font-semibold">Jobpilot</h1>
          </div>

          <div style={{ width: 260 }}>
            <div className="flex justify-between text-sm mb-1">
              <span className="text-gray-500">Setup Progress</span>
              <span className="text-blue-600 font-medium">
                {progress}% Completed
              </span>
            </div>
            <div className="progress-track">
              <div className="progress-bar" style={{ width: `${progress}%` }} />
            </div>
          </div>
        </header>

        <div className="bg-white rounded shadow-sm p-8">
          <Stepper steps={steps} current={current} setCurrent={setCurrent} />

          <div className="mt-6">
            {current === 1 && (
              <CompanyInfo data={data} update={update} next={next} />
            )}
            {current === 2 && (
              <FoundingInfo
                data={data}
                update={update}
                next={next}
                prev={prev}
              />
            )}
            {current === 3 && (
              <SocialLinks
                data={data}
                update={update}
                next={next}
                prev={prev}
              />
            )}
            {current === 4 && (
              <Contact
                data={data}
                update={update}
                submit={submit}
                prev={prev}
              />
            )}
            {current === 5 && <Complete />}
          </div>
        </div>
      </div>
    </div>
  );
}
