import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { saveCompany, fetchCompany } from "../../store/slices/companySlice.js";
import ImageUploader from "../../components/ImageUploader.jsx";

const steps = ["Company Info","Founding Info","Social Links","Contact"];

export default function CompanyMultiStep() {
  const dispatch = useDispatch();
  const { profile } = useSelector(s => s.company);
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    company_name: "", address: "", city: "", state: "", country: "",
    postal_code: "", website: "", industry: "", founded_date: "", description: "",
    social_links: {}, phone: "", map_location: "", email: ""
  });

  useEffect(()=> { dispatch(fetchCompany()); }, [dispatch]);

  useEffect(()=> {
    if (profile) {
      setForm(prev => ({ ...prev, ...profile }));
    }
  }, [profile]);

  const onNext = async () => {
    if (step === steps.length - 1) {
      // finalize (save)
      await dispatch(saveCompany(form));
      setStep(step+1);
    } else {
      setStep(step+1);
    }
  };

  const onPrev = () => setStep(Math.max(0, step-1));

  return (
    <div className="container mx-auto p-8">
      <div className="card p-6">
        <h2 className="text-xl font-semibold mb-4">{steps[step]}</h2>

        {step === 0 && (
          <div>
            <label>Company Name</label>
            <input value={form.company_name||""} onChange={e=>setForm({...form, company_name:e.target.value})} />
            <label>About</label>
            <textarea value={form.description||""} onChange={e=>setForm({...form, description:e.target.value})} />
            <div className="mt-4">
              <ImageUploader type="logo" onUploaded={(url)=>setForm({...form, logo_url:url})}/>
              <ImageUploader type="banner" onUploaded={(url)=>setForm({...form, banner_url:url})}/>
            </div>
          </div>
        )}

        {step === 1 && (
          <div>
            <label>Industry</label>
            <input value={form.industry||""} onChange={e=>setForm({...form, industry:e.target.value})} />
            <label>Founded date</label>
            <input type="date" value={form.founded_date||""} onChange={e=>setForm({...form, founded_date:e.target.value})} />
            <label>Website</label>
            <input value={form.website||""} onChange={e=>setForm({...form, website:e.target.value})} />
          </div>
        )}

        {step === 2 && (
          <div>
            <label>Social Links (JSON)</label>
            <textarea value={JSON.stringify(form.social_links || {})} onChange={e=>{
              try { setForm({...form, social_links: JSON.parse(e.target.value)}) } catch(e) {}
            }} />
            <small>Example: {"{\"facebook\":\"https://...\",\"twitter\":\"https://...\"}"}</small>
          </div>
        )}

        {step === 3 && (
          <div>
            <label>Phone</label>
            <input value={form.phone||""} onChange={e=>setForm({...form, phone:e.target.value})} />
            <label>Email</label>
            <input value={form.email||""} onChange={e=>setForm({...form, email:e.target.value})} />
            <label>Map location</label>
            <input value={form.map_location||""} onChange={e=>setForm({...form, map_location:e.target.value})} />
          </div>
        )}

        {step >= steps.length && (
          <div>
            <h3>ðŸŽ‰ Congratulations â€” profile complete!</h3>
            <button onClick={()=>window.location.href="/dashboard"}>View Dashboard</button>
          </div>
        )}

        <div className="flex gap-3 mt-6">
          {step > 0 && <button onClick={onPrev} className="btn-ghost">Previous</button>}
          {step < steps.length && <button onClick={onNext} className="btn-primary">{step === steps.length-1 ? "Finish" : "Save & Next"}</button>}
        </div>
      </div>
    </div>
  );
}
