import React, { useState } from "react";
import DashboardSidebar from "../../components/DashboardSidebar";
import CompanySettings from "./tabs/CompanySettings";
import FoundingSettings from "./tabs/FoundingSettings";
import SocialSettings from "./tabs/SocialSettings";
import AccountSettings from "./tabs/AccountSettings";

const tabs = [
  { id: "company", label: "Company Info" },
  { id: "founding", label: "Founding Info" },
  { id: "social", label: "Social Media Profile" },
  { id: "account", label: "Account Setting" }
];

export default function SettingsPage() {
  const [sidebarActive, setSidebarActive] = useState("Settings");
  const [activeTab, setActiveTab] = useState("company");

  // flat state: logo/banner are preview URLs (strings)
  const [data, setData] = useState({
    logo: "",
    banner: "",
    companyName: "",
    about: "",
    orgType: "",
    industry: "",
    teamSize: "",
    year: "",
    website: "",
    vision: "",
    socials: [{ platform: "facebook", url: "" }, { platform: "twitter", url: "" }],
    mapLocation: "",
    phoneCountry: "+91",
    phone: "",
    email: "",
    currentPassword: "",
    newPassword: "",
    confirmPassword: ""
  });

  const update = (patch) => setData(prev => ({ ...prev, ...patch }));

  const saveChanges = () => {
    // replace with API call
    console.log("Saved settings:", data);
    alert("Save (console) - replace with API call");
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="border-b bg-white py-3">
        <div className="max-w-6xl mx-auto px-6 flex items-center justify-between">
          <div className="text-sm text-gray-600">Home &nbsp; Find Candidate &nbsp; <span className="text-blue-600">Dashboard</span></div>
          <div className="text-sm text-gray-600">+1-202-555-0178</div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-6 py-8 flex gap-8">
        <DashboardSidebar active={sidebarActive} setActive={setSidebarActive} />

        <main className="flex-1 bg-white rounded shadow-sm p-8">
          <div className="flex items-start justify-between mb-6">
            <h2 className="text-xl font-semibold">Settings</h2>
            <div className="text-sm text-gray-500">© Company</div>
          </div>

          <div className="border-b mb-6">
            <nav className="flex gap-6 text-sm text-gray-600">
              {tabs.map(t => (
                <button key={t.id}
                        onClick={() => setActiveTab(t.id)}
                        className={`py-3 px-1 ${activeTab === t.id ? "border-b-2 border-blue-600 text-blue-600" : "hover:text-gray-800"}`}>
                  {t.label}
                </button>
              ))}
            </nav>
          </div>

          <div>
            {activeTab === "company" && <CompanySettings data={data} update={update} save={saveChanges} />}
            {activeTab === "founding" && <FoundingSettings data={data} update={update} save={saveChanges} />}
            {activeTab === "social" && <SocialSettings data={data} update={update} save={saveChanges} />}
            {activeTab === "account" && <AccountSettings data={data} update={update} save={saveChanges} />}
          </div>
        </main>
      </div>
    </div>
  );
}
