import React, { useRef, useEffect } from "react";

export default function CompanySettings({ data, update, save }) {
  const logoRef = useRef(null);
  const bannerRef = useRef(null);

  const onFile = (e, key) => {
    const file = e.target.files?.[0];
    if (!file) return;
    const url = URL.createObjectURL(file);
    update({ [key]: url });
    if (key === "logo") logoRef.current = file;
    if (key === "banner") bannerRef.current = file;
  };

  useEffect(() => {
    return () => {
      if (data.logo) URL.revokeObjectURL(data.logo);
      if (data.banner) URL.revokeObjectURL(data.banner);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <h3 className="text-base font-medium mb-4">Logo & Banner Image</h3>

      <div className="flex gap-6 mb-4 items-start">
        <div className="w-48">
          <label className="text-sm text-gray-600 block mb-2">Upload Logo</label>
          <div className="border rounded overflow-hidden">
            {data.logo ? (
              <img src={data.logo} alt="logo" className="w-full h-36 object-cover" />
            ) : (
              <div className="h-36 flex items-center justify-center text-gray-400">No logo</div>
            )}
            <input type="file" accept="image/*" className="w-full" onChange={(e)=>onFile(e,"logo")} />
          </div>
        </div>

        <div className="flex-1">
          <label className="text-sm text-gray-600 block mb-2">Banner Image</label>
          <div className="border rounded overflow-hidden">
            {data.banner ? (
              <img src={data.banner} alt="banner" className="w-full h-36 object-cover" />
            ) : (
              <div className="h-36 flex items-center justify-center text-gray-400">No banner</div>
            )}
            <input type="file" accept="image/*" className="w-full" onChange={(e)=>onFile(e,"banner")} />
          </div>
        </div>
      </div>

      <div className="mb-4">
        <label className="text-sm text-gray-600 block mb-1">Company name</label>
        <input value={data.companyName} onChange={(e)=>update({companyName: e.target.value})} className="w-full border rounded p-2" />
      </div>

      <div className="mb-6">
        <label className="text-sm text-gray-600 block mb-1">About us</label>
        <textarea value={data.about} onChange={(e)=>update({about: e.target.value})} className="w-full border rounded p-2 h-28" placeholder="Write about your company..." />
      </div>

      <div>
        <button onClick={save} className="bg-blue-600 text-white px-4 py-2 rounded">Save Change</button>
      </div>
    </div>
  );
}
