import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import api from "../../api/api.js";

export const fetchCompany = createAsyncThunk("company/fetch", async (_, { rejectWithValue }) => {
  try {
    const res = await api.get("/api/company/profile");
    return res.data.profile;
  } catch (err) {
    return rejectWithValue(err.response?.data);
  }
});

export const saveCompany = createAsyncThunk("company/save", async (payload, { rejectWithValue }) => {
  try {
    const res = await api.post("/api/company/profile", payload);
    return res.data.profile;
  } catch (err) {
    return rejectWithValue(err.response?.data);
  }
});

const companySlice = createSlice({
  name: "company",
  initialState: { profile: null, loading: false, error: null },
  extraReducers: (builder) => {
    builder
      .addCase(fetchCompany.pending, (s) => { s.loading = true; s.error = null; })
      .addCase(fetchCompany.fulfilled, (s, a) => { s.loading = false; s.profile = a.payload; })
      .addCase(fetchCompany.rejected, (s, a) => { s.loading = false; s.error = a.payload; })
      .addCase(saveCompany.pending, (s) => { s.loading = true; s.error = null; })
      .addCase(saveCompany.fulfilled, (s, a) => { s.loading = false; s.profile = a.payload; })
      .addCase(saveCompany.rejected, (s, a) => { s.loading = false; s.error = a.payload; });
  }
});

export default companySlice.reducer;
