commits.content:
@tailwind base;
@tailwind components;
@tailwind utilities;


@layer components {
  .phone-input .flag-dropdown {
    @apply bg-transparent border-0 shadow-none hover:bg-transparent focus:bg-transparent !important;
  }

  .phone-input .selected-flag {
    @apply bg-transparent p-0 border-0 shadow-none !important;
  }
}
